mapper.select:

SELECT RV_NO, NO, MV_TITLE, MV_POSTER
     , RV_NICK, RV_RATE, RV_TITLE, RV_CONTENT
     , RV_CONTENT, RV_DATE
FROM (SELECT ROWNUM R_NUM, R.*, M.*
      FROM (SELECT *
            FROM REVIEW
            ORDER BY RV_DATE DESC
            ) R, MOVIE M
      WHERE R.MV_NO = M.MV_NO
      AND ROWNUM <= #{end}
)
 WHERE R_NUM >= #{begin}
 ORDER BY RV_DATE DESC


SELECT RV_NO, NO, MV_TITLE, MV_POSTER
     , RV_NICK, RV_RATE, RV_TITLE, RV_CONTENT
     , RV_CONTENT, RV_DATE, RV_REC, RV_WARN
FROM (SELECT ROWNUM R_NUM, R.*, M.*
      FROM (SELECT *
            FROM REVIEW
            ORDER BY RV_NO DESC
            ) R, MOVIE M
      WHERE R.MV_NO = M.MV_NO
      AND ROWNUM <= #{end}
)
 WHERE R_NUM >= #{begin}
 ORDER BY RV_DATE DESC

   		SELECT A.*, M.*,  R.*
		FROM ACCOUNT A, MOVIE M, REVIEW R
		WHERE R.NO = A.NO
		AND R.MV_NO = M.MV_NO
		AND TO_CHAR(RV_DATE, 'YYYY') = #{year}
		AND TO_CHAR(RV_DATE, 'MM') = #{month}
