mapper.select:

   SELECT *
   FROM (SELECT ROWNUM R_NUM, P.*
         FROM (SELECT *
               FROM POST
               ORDER BY PS_NO DESC
              ) P
         WHERE ROWNUM <= #{end}
        )
   WHERE R_NUM >= #{begin}

SELECT COUNT(*) AS CNT FROM POST

   
      WHERE PS_TITLE = #{keyword}
   
   
      WHERE TO_CHAR(PS_DATE, 'YYYYMMDD') = #{keyword}
   
   
      AND 1 = 2

mapper.update:
UPDATE POST
   SET PS_TITLE = #{psTitle} 
      , PS_CONTENT = #{psContent}
      , PS_DATE = SYSDATE
      , PS_FILE = #{psFile}
      , PS_ORIFILE = #{psOrifile}
   WHERE PS_NO = #{psNo}

UPDATE POST
SET PS_NICK = #{nick}
WHERE NO = #{no}
