mapper.select:
SELECT R.*
FROM REVIEW R, MOVIE M
WHERE R.MV_NO = M.MV_NO
AND R.MV_NO = #{mvNO}
AND R.RV_NO = #{rvNo}
ORDER BY R.RV_NO DESC

mapper.update:
UPDATE REVIEW
SET RV_RATE = #{rvRate},
    RV_TITLE = #{rvTitle},
    RV_CONTENT = #{rvContent}
WHERE RV_NO = #{rvNo}

UPDATE REVIEW
SET RV_NICK = #{nick}
WHERE NO = #{no}
